/*时间轴*/
.aui-timeline {
	position: relative;
	padding: 0;
	list-style: none;
}

.aui-timeline:before {
	content: '';
	position: absolute;
	top: 0;
	left: 1.85rem;
	width: 2px;
	height: 100%;
	background: #ececec;
	z-index: 0;
}

.aui-timeline .aui-timeline-item {
	position: relative;
	margin-bottom: 0.75rem;
}

.aui-timeline .aui-timeline-item-header {
	background-color: #ececec;
	padding: 0.2rem 0.5rem;
	margin: 0.75rem;
	text-align: center;
	display: inline-block;
	position: relative;
	z-index: 1;
	font-size: 0.7rem;
}

.aui-timeline .aui-timeline-item-label {
	width: 2.5rem;
	height: 1.5rem;
	line-height: 1.5rem;
	font-size: 0.7em;
	background-color: #ececec;
	position: absolute;
	text-align: center;
	left: 0.75rem;
	top: 0;
}

.aui-timeline .aui-timeline-item-label-icon {
	width: 1.5rem;
	height: 1.5rem;
	font-size: 0.7rem;
	line-height: 1.5rem;
	background-color: #ececec;
	position: absolute;
	border-radius: 50%;
	text-align: center;
	left: 1.15rem;
	top: 0;
}

.aui-timeline .aui-timeline-item-inner {
	margin-left: 3.75rem;
	margin-right: 0.75rem;
	padding: 0;
	position: relative;
}

.aui-list-wid label {
	width: 103px;
	display: inline-block;
	font-size: 12px;
}

/*基础动画类*/
@keyframes rotate {
	0% {
		-webkit-transform: rotate(0deg) scale(1);
		transform: rotate(0deg) scale(1);
	}

	50% {
		-webkit-transform: rotate(180deg) scale(1);
		transform: rotate(180deg) scale(1);
	}

	100% {
		-webkit-transform: rotate(360deg) scale(1);
		transform: rotate(360deg) scale(1);
	}
}

@-webkit-keyframes rotate {
	0% {
		-webkit-transform: rotate(0deg) scale(1);
		transform: rotate(0deg) scale(1);
	}

	50% {
		-webkit-transform: rotate(180deg) scale(1);
		transform: rotate(180deg) scale(1);
	}

	100% {
		-webkit-transform: rotate(360deg) scale(1);
		transform: rotate(360deg) scale(1);
	}
}

@keyframes bounce {
	0%, 100% {
		-webkit-transform: scale(0.0);
		transform: scale(0.0);
	}

	50% {
		-webkit-transform: scale(1.0);
		transform: scale(1.0);
	}
}

@-webkit-keyframes bounce {
	0%, 100% {
		-webkit-transform: scale(0.0);
		transform: scale(0.0);
	}

	50% {
		-webkit-transform: scale(1.0);
		transform: scale(1.0);
	}
}

@keyframes fadeIn {
	from {
		opacity: 0.3;
	}

	to {
		opacity: 1;
	}
}

@-webkit-keyframes fadeIn {
	from {
		opacity: 0.3;
	}

	to {
		opacity: 1;
	}
}

